mapper.select:

{ CALL  PKG_PDS.PROC_PDS_PAGINGLIST(
   #{menu_id,    mode=IN,  jdbcType=VARCHAR },
   #{nowpage,    mode=IN,  jdbcType=INTEGER },
   #{pagecount,  mode=IN,  jdbcType=INTEGER },
   #{totalcount, mode=OUT, jdbcType=INTEGER },
   #{result,     mode=OUT, jdbcType=CURSOR,
       javaType=ResultSet, resultMap = PdsMap}
) }

mapper.insert:
{CALL PKG_PDS.PROC_PDS_INSERT( #{menu_id, mode=IN, jdbcType=VARCHAR }, #{title, mode=IN, jdbcType=VARCHAR }, #{cont, mode=IN, jdbcType=VARCHAR }, #{writer, mode=IN, jdbcType=VARCHAR }, #{bnum, mode=IN, jdbcType=INTEGER }, #{lvl, mode=IN, jdbcType=INTEGER }, #{step, mode=IN, jdbcType=INTEGER }, #{nref, mode=IN, jdbcType=INTEGER },
#{filenames, mode=IN, jdbcType=Object, jdbcType = ARRAY, jdbcTypeName = FILE_ARRAY, typeHandler = com.green.pdssp.service.typehandler.ListMapTypeHanlder }, #{fileexts, mode=IN, jdbcType=Object, jdbcType = ARRAY, jdbcTypeName= FILE_ARRAY, typeHandler = com.green.pdssp.service.typehandler.ListMapTypeHanlder }, #{sfilenames, mode=IN, jdbcType=Object, jdbcType = ARRAY, jdbcTypeName= FILE_ARRAY, typeHandler = com.green.pdssp.service.typehandler.ListMapTypeHanlder }
) }

mapper.select:
{ CALL  PKG_PDS.PROC_PDS_VIEW(
    #{ idx,     mode=IN,  jdbcType=INTEGER },
    #{ result,  mode=OUT, jdbcType=CURSOR,
       javaType  = ResultSet,
       resultMap = PdsMap }          
) }

        {CALL  PKG_PDS.PROC_PDS_FILELIST(
	        #{ idx,     mode=IN,   jdbcType=INTEGER },
	        #{ result,  mode=OUT,  jdbcType=CURSOR,
	           javaType = ResultSet,
	           resultMap = FileListMap }   
        )}
